import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './App.css';




const App = () => {
  const [inputs, setInputs] = useState({});
  const rates = {
    usd: 180,
    eur: 220,
    inr: 2.5
  }

  return (
    <div className="container">
      <div className="title">
        Currency Convertor
      </div>
      <label>SriLankan Rupees</label>
      <input 
        type="number" 
        name="txtslrs"
        id="txtslrs"
        placeholder="currency in slrs"
        required>
      </input>
      <br></br>
      <label>Choose a Currency Type</label>
      <select className="select">
        {Object.keys(rates).map((currency) => {
          return (
            <option className="option" value={rates[currency]}>{currency}</option>
                  )
         })
        }
      </select>
      <br></br>
      <button className="button" onClick={(alertName) => {
        alert ($(inputs.txtslrs))

      }}>Convert</button>
      <br></br>
      <input 
          type="number"
          name="convertedrs"
          id="convertedrs"
          readOnly>    
      </input>



</div>
  );
    }

    export default App;
