import React, {useState} from 'react';

import './App.css';




const App = () => {
  const rates = {
    usd: 180,
    eur: 220,
    inr: 2.5
  }

  return (
    <div className="container">
      <div className="title">
        Currency Convertor
      </div>
      <label>SriLankan Rupees</label>

      <select className="select">
        {Object.keys(rates).map((currency) => {
          return (
            <option className="option" value={rates[currency]}>{currency}</option>
          )
        })}
      </select>



      <button className="button" onClick={(alertName) => {
        alert(alertName)

      }}>Convert</button>




</div>
  );
    }
    export default App;
