import React, {useState} from 'react';
import './App.css';




const App = () => {
  const [inputs, setInputs] = useState({});
  const handleInputChange = (event) => {
    event.persist();
    setInputs(inputs => ({...inputs, [event.target.name]: event.target.value}));
  }
  const [choice,setOption]=useState({});
  const handleOptionChange=(ev)=>{

    setOption(choice=>(choice[ev.target.value]))

  }
  const rates = {
    usd: 180,
    eur: 220,
    inr: 2.5
  }

  return (
    <div className="container">
      <div className="title">
        Currency Convertor
      </div>
      <label>SriLankan Rupees</label>
      <input 
        type="number" 
        name="txtslrs"
        id="txtslrs"
        placeholder="currency in slrs"
        required
        onChange={handleInputChange}
        value={inputs.txtslrs}>
      </input>
      <br></br>
      <label>Choose a Currency Type</label>
      <select 
      className="select"
      value={choice}
      onChange={ev=>setOption(ev.currentTarget.value)}>
        {Object.keys(rates).map((currency) => {
          return (

            <option className="option" value={rates[currency]}>{currency}</option>
                  )
         })
        }
      </select>
      <br></br>
      <input 
          type="number"
          name="convertedrs"
          id="convertedrs"
          value={(choice)*(inputs.txtslrs)}
          readOnly>    
      </input>



</div>
  );
    }

    export default App;
