import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import './App.css';




const App = () => {
  const [inputs, setInputs] = useState({});
  const handleInputChange = (event) => {
    event.persist();
    setInputs(inputs => ({...inputs, [event.target.name]: event.target.value}));
  }
  const [choice,setOption]=useState({});
  const handleOptionChange=(ev)=>{
    choice[ev.target.value]
  }
  const rates = {
    usd: 180,
    eur: 220,
    inr: 2.5
  }

  return (
    <div className="container">
      <div className="title">
        Currency Convertor
      </div>
      <label>SriLankan Rupees</label>
      <input 
        type="number" 
        name="txtslrs"
        id="txtslrs"
        placeholder="currency in slrs"
        required
        onChange={handleInputChange}
        value={inputs.txtslrs}>
      </input>
      <br></br>
      <label>Choose a Currency Type</label>
      <select className="select">
        {Object.keys(rates).map((currency) => {
          return (
            <option className="option" value={rates[currency]}>{currency}</option>
                  )
         })
        }
      </select>
      <br></br>
      <button className="button" onClick={() => {
        alert ((choice))

      }}>Convert</button>
      <br></br>
      <input 
          type="number"
          name="convertedrs"
          id="convertedrs"
          readOnly>    
      </input>



</div>
  );
    }

    export default App;
