import React from 'react';
import ReactDOM from 'react-dom';
import './App.css';




const App = () => {
  const rates = {
    usd: 180,
    eur: 220,
    inr: 2.5
  }

  return (
    <div className="container">
      <div className="title">
        Currency Convertor
      </div>
      <label>SriLankan Rupees</label>
      <input 
        type="number" 
        placeholder="currency in slrs">
      </input><br></br>
      <label>Choose a Currency Type</label>
      <select className="select">
        {Object.keys(rates).map((currency) => {
          return (
            <option className="option" value={rates[currency]}>{currency}</option>
          )
        })}
      </select>


      <br></br>
      <button className="button" onClick={(alertName) => {
        alert(alertName)

      }}>Convert</button>
      <br></br>
      <input 
          type="number"
          name="convertedrs"
          id="convertedrs"></input>



</div>
  );
    }

    export default App;
